data.css:
html, body {
    width: 100%;
    height: 100%;
    text-align: center;
    overflow: hidden;
    position: relative;
    background: #0a0a0a;
    animation: fade-bg .5s ease;
}

.container {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.main {
    font-size: 200%;
    opacity: .3;
    color: white;
    display: block;
    line-height: 1.2;
    padding-bottom: 1rem;
    animation: fade-main .3s ease;
}

.slogan {
    font-size: 150%;
    opacity: .25;
    color: white;
    animation: fade-slogan .5s ease;
}

.t {
    color: #e20074;
}

a:link, a:visited {
    text-decoration: none;
    color: #444;
}

a:hover, a:active {
    text-decoration: none;
    color: #666;
}

#schlagzeilen {
    list-style: none;
    margin: 20px;
    padding: 0;
}

@keyframes fade-bg {
    from { background: #111; }
    to   { background: #0a0a0a; }
}


@keyframes fade-main {
    from { opacity: 0; transform: translateY(.375rem); }
    to   { opacity: .3; transform: translateY(0); }
}

@keyframes fade-slogan {
    from { opacity: 0; transform: translateY(.5rem); }
    to   { opacity: .25; transform: translateY(0); }
}

data.javascript:
const schlagzeilen = document.getElementById('schlagzeilen')

fetch('https://tonapi.evilco.net/schlagzeilen')
.then(res => res.json())
.then((json) => {
    json.forEach((schlagzeile) => {
        const li = document.createElement('li');
        const a = document.createElement('a')
        a.href = schlagzeile.url
        a.onclick = (evt) => {
            evt.preventDefault()
            window.open(schlagzeile.url)
        }
        a.innerHTML = schlagzeile.text
        li.appendChild(a)
        schlagzeilen.appendChild(li)
    })
})
