data.css:
html, body {
    width: 100%;
    height: 100%;
    text-align: center;
    overflow: hidden;
    position: relative;
    background: #0a0a0a;
    animation: fade-bg .5s ease;
}

.container {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

a.advert:link,
a.advert:visited
{
    color: #fff;
    text-decoration: none;
}

a.advert:hover {
    color: #999;
}

.header {
    font-size: 200%;
    opacity: .3;
    color: white;
    display: block;
    line-height: 1.2;
    padding-bottom: 1rem;
    animation: fade-header .3s ease;
}
.commit {
    font-size: 150%;
    opacity: .25;
    color: white;
    animation: fade-commit .5s ease;
}

@keyframes fade-bg {
    from { background: #111; }
    to   { background: #0a0a0a; }
}


@keyframes fade-header {
    from { opacity: 0; transform: translateY(.375rem); }
    to   { opacity: .3; transform: translateY(0); }
}

@keyframes fade-commit {
    from { opacity: 0; transform: translateY(.5rem); }
    to   { opacity: .25; transform: translateY(0); }
}

data.javascript:
document.querySelector('a.advert').addEventListener('click', function(evt) {
    evt.preventDefault();
    window.open(this.getAttribute('href'));
});

fetch('https://evilcdn.net/whatthecommit/commit_messages.json', {
    mode: 'cors',
    cache: 'no-cache',
    method: 'GET'
})
.then((res => res.json()))
.then((json) => {
    const msgs = json.messages;
    const msg = msgs[Math.floor(Math.random()*msgs.length)];
    document.getElementById('commit').innerHTML = msg;
})
